import { Component } from '@angular/core';
import {FormControl, FormGroup } from '@angular/forms';
import { CalculoCDBRequest, CdbService } from '../Service/cdb.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})

export class HomeComponent {
  meses: number = 0;
  valorbruto: number = 0;
  valorliquido: number = 0;

  constructor(private cdbService: CdbService) { }

  form = new FormGroup({
    meses: new FormControl<number | null>(null),
    valorInicial: new FormControl<number | null>(null),
  });

  onSubmit(e: any) {
    e.preventDefault();

    const request: CalculoCDBRequest = {
      ValorInicial: 1200,
      Meses: 12
    }

    this.cdbService.getPosts(request)
      .subscribe(res => {
        this.meses = 12;
        this.valorbruto = res.valorFinal;
        this.valorliquido = res.valorFinal - res.impostoValor
      });
  }
}
<h1>Calculo CDB</h1>
<p>Taxa CDI: 108%</p>
<p>Taxa TB: 0,9%</p>
<div class="painel-form">
  <div class="panel-form-fields">
    <form [formGroup]="form" (submit)="onSubmit($event)">
      <mat-form-field class="example-full-width">
        <mat-label>Valor Inicial</mat-label>
        <span matPrefix>R$ &nbsp;</span>
        <input type="number" matInput placeholder="0,00">
      </mat-form-field>

      <mat-form-field class="example-full-width">
        <mat-label>Meses</mat-label>
        <input type="number" matInput placeholder="0">
      </mat-form-field>

      <button mat-raised-button color="primary">Calcular</button>
    </form>
  </div>
  <div class="painel-form-resultado">
    <p>Em {{ meses }} você teria:</p>
    <p>{{ valorbruto }}</p>
    <p>{{ valorliquido }}</p>
  </div>
</div>
