import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  meses: number = 0;
  valorbruto: number = 0;
  valorliquido: number = 0;
}
<h1>Calculo CDB</h1>
<p>Taxa CDI: 108%</p>
<p>Taxa TB: 0,9%</p>
<div class="painel-form">
  <div></div>
  <div class="painel-form-resultado">
    <p>Em {{ meses }} você teria:</p>
    <p>{{ valorbruto }}</p>
    <p>{{ valorliquido }}</p>
  </div>
</div>
